import React, { useState } from 'react';
import RecipeForm from "./RecipeForm";

const Menu = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isRecipeFormVisible, setRecipeFormVisible] = useState(false); // Новое состояние

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    // Функция для открытия формы добавления рецепта
    const handleAddRecipeClick = () => {
        setRecipeFormVisible(true);
    };

    return (
        <div className="menu">
            <button className="menu-button" onClick={toggleMenu}>
                Menu
            </button>
            {isOpen && (
                <div className="menu-items">
                    <p onClick={toggleMenu}>My menu</p>
                    <p onClick={() => setIsOpen(false)}>Add Recipe</p>
                    <p onClick={toggleMenu}>Schedule</p>
                    <p onClick={toggleMenu}>My plan</p>
                </div>
            )}
            {/* Передайте состояние isRecipeFormVisible в компонент RecipeForm */}
            {isRecipeFormVisible && <RecipeForm isVisible={isRecipeFormVisible} onClose={() => setRecipeFormVisible(false)} />}
        </div>
    );
};

export default Menu;
