import React, { useState } from 'react';
import RecipeForm from "./RecipeForm";


const Menu = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isRecipeFormVisible, setIsRecipeFormVisible] = useState(false);

    // Функция для открытия/закрытия меню
    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };
    const toggleRecipeForm = () => {
        setIsRecipeFormVisible(!isRecipeFormVisible);
    };


    return (
        <div className="menu">
            <button className="menu-button" onClick={toggleMenu}>
                Menu
            </button>
            {/* Если меню открыто, показываем его содержимое */}
            {isOpen && (
                <div className="menu-items">
                    <p onClick={toggleMenu}>My menu</p>
                   <button> <p onClick={toggleRecipeForm}>Add Recipe</p> </button>
                    <p onClick={toggleMenu}>Schedule</p>
                    <p onClick={toggleMenu}>My plan</p>
                </div>
            )}
            {/* Отображаем форму рецепта, если isRecipeFormVisible равно true */}
            {isRecipeFormVisible && (
                <div className="recipe-form-container">
                    <RecipeForm />
                </div>
            )}
        </div>
    );
};

export default Menu;
