// App.js

// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Menu from './components/Menu';
import Basket from './components/Basket';
import RecipeList from './components/RecipeList';
import AddRecipePage from './components/pages/AddRecipePage';
import PlanSchedule from './components/PlanSchedule';
import RecipePage from './components/pages/RecipePage'; // Импортируем новую страницу RecipePage
import Plan from './components/Plan';
import './index.css';

function App() {
    const [showBasket, setShowBasket] = useState(false);

    const toggleBasket = () => {
        setShowBasket(!showBasket);
    };

    const addToBasket = (ingredient) => {
        fetch('/add-to-basket', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ ingredient: ingredient })
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    console.log('Ingredient added to basket successfully');
                } else {
                    console.error('Failed to add ingredient to basket:', data.message);
                }
            })
            .catch(error => console.error('Error adding ingredient to basket:', error));
    };
    return (

        <Router>
            <div className="App">
                <Menu />
                <div className="content">
                    <Routes>
                        <Route path="/my-recipes" element={<RecipeList />} />
                        <Route path="/add-recipe" element={<AddRecipePage />} />
                        <Route path="/schedule" element={<PlanSchedule />} />
                        <Route path="/plan" element={<Plan />} />
                        <Route
                            path="/recipe/:id"
                            element={<RecipePage />}
                        />
                    </Routes>
                </div>
                <button onClick={toggleBasket}>Toggle Basket</button>
                {showBasket && <Basket />} {/* Отображение корзины при showBasket === true */}
            </div>
        </Router>
    );
}

export default App;
