import React, { useState, useEffect } from 'react';

const Plan = () => {
    const [caloriePlan, setCaloriePlan] = useState(null);

    useEffect(() => {
        fetchCaloriePlan();
    }, []);

    const fetchCaloriePlan = () => {
        fetch('/api/recipes/get-plan')
            .then((response) => response.json())
            .then((data) => {
                setCaloriePlan(data.plan);
            })
            .catch((error) => console.error('Error fetching calorie plan:', error));
    };

    return (
        <div className="plan">
            <h2>Calorie Plan</h2>
            {caloriePlan ? <p>Your daily calorie plan is {caloriePlan} calories.</p> : <p>Loading...</p>}
        </div>
    );
};

export default Plan;
