import React, { useState } from 'react';
import axios from 'axios';

const AddRecipe = () => {
    const [recipeData, setRecipeData] = useState({
        image: '',
        name: '',
        ingredients: '',
        calories: '',
        instructions: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipeData({ ...recipeData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/recipes/create', recipeData);
            console.log(response.data);

        } catch (error) {
            console.error('Error creating recipe:', error);

        }
    };

    return (
        <div>
            <h2>Add Recipe</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Image URL:
                    <input type="text" name="image" value={recipeData.image} onChange={handleChange} />
                </label>
                <label>
                    Name:
                    <input type="text" name="name" value={recipeData.name} onChange={handleChange} />
                </label>
                <label>
                    Ingredients:
                    <textarea name="ingredients" value={recipeData.ingredients} onChange={handleChange} />
                </label>
                <label>
                    Calories:
                    <input type="text" name="calories" value={recipeData.calories} onChange={handleChange} />
                </label>
                <label>
                    Instructions:
                    <textarea name="instructions" value={recipeData.instructions} onChange={handleChange} />
                </label>
                <button type="submit">Create Recipe</button>
            </form>
        </div>
    );
};

export default AddRecipe;
