import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ShoppingCart = () => {
    const [basket, setBasket] = useState([]);

    useEffect(() => {
        axios.get('/api/recipes/get-basket')
            .then(res => {
                setBasket(res.data.basket);
            })
            .catch(err => {
                console.log(err);
            });
    }, []);

    const handleMarkAsPurchased = (ingredientId) => {
        axios.put('/api/recipes/mark-as-purchased', { ingredientId })
            .then(res => {
                const updatedBasket = basket.map(item => {
                    if (item.id === ingredientId) {
                        return { ...item, purchased: true };
                    }
                    return item;
                });
                setBasket(updatedBasket);
            })
            .catch(err => {
                console.log(err);
            });
    };

    return (
        <div className="shopping-cart">
            <h2>Shopping Cart</h2>
            <ul>
                {basket.map(item => (
                    <li key={item.id}>
                        {item.name} {item.purchased ? "(Purchased)" : ""}
                        {!item.purchased && (
                            <button onClick={() => handleMarkAsPurchased(item.id)}>Mark as Purchased</button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ShoppingCart;
