import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Menu from './components/Menu';
import Basket from './components/Basket';
import RecipeList from './components/RecipeList';
import AddRecipePage from './components/pages/AddRecipePage';
import PlanSchedule from './components/PlanSchedule';
import RecipePage from './components/pages/RecipePage';
import Plan from './components/Plan';
import './index.css';

function App() {
    const [showBasket, setShowBasket] = useState(false);
    const [currentPath, setCurrentPath] = useState('');

    useEffect(() => {
        // Установка текущего пути при загрузке приложения
        setCurrentPath(window.location.pathname);
        // Проверка пути при каждом изменении URL
        window.onpopstate = () => {
            setCurrentPath(window.location.pathname);
        };
    }, []);

    const toggleBasket = () => {
        setShowBasket(!showBasket);
    };

    return (
        <Router>
            <div className="App">
                <Menu />
                <div className="content">
                    <Routes>
                        <Route
                            path="/my-recipes"
                            element={<RecipeList currentPath={currentPath} />}
                        />
                        <Route
                            path="/add-recipe"
                            element={<AddRecipePage currentPath={currentPath} />}
                        />
                        <Route
                            path="/schedule"
                            element={<PlanSchedule currentPath={currentPath} />}
                        />
                        <Route path="/plan" element={<Plan currentPath={currentPath} />} />
                        <Route
                            path="/recipe/:id"
                            element={<RecipePage currentPath={currentPath} />}
                        />
                    </Routes>
                </div>
                <button onClick={toggleBasket}>Toggle Basket</button>
                {showBasket && <Basket />}
                {currentPath === '/' && (
                    <footer className="footer">
                        <img src="/MealPlannerlogo.png" alt="Logo" className="footer-logo" />
                    </footer>
                )}
            </div>
        </Router>
    );
}

export default App;
