import React from 'react';

const MyMenu = ({ recipes }) => {
    return (
        <div>
            <h2>My Menu</h2>
            <ul>
                {recipes.map((recipe, index) => (
                    <li key={index}>
                        <strong>{recipe.name}</strong> {/* Здесь выводите название рецепта */}
                        <p>Ingredients: {recipe.ingredients}</p> {/* Здесь выводите ингредиенты */}
                        <p>Method: {recipe.method}</p> {/* Здесь выводите способ приготовления */}
                        <p>Calories: {recipe.calories}</p> {/* Здесь выводите калорийность */}
                        <img src={recipe.photo} alt="Meal" /> {/* Здесь выводите фото блюда */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MyMenu;
