import React, { useState } from 'react';

const RecipeForm = ({ isVisible, onClose, addRecipe }) => {
    // State for storing recipe data
    const [recipeData, setRecipeData] = useState({
        photo: '',
        ingredients: '',
        method: '',
        calories: ''
    });

    // Handler for changes in form fields
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setRecipeData({ ...recipeData, [name]: value });
    };

    // Form submission handler
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Sending data to the server
            const response = await fetch('http://localhost:3001/map', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(recipeData)
            });
            if (!response.ok) {
                throw new Error('Adding error');
            }
            // Getting the added recipe from the server response
            const newRecipe = await response.json();
            // Adding a new recipe to the list of recipes
            addRecipe(newRecipe);
            // Resetting form data
            setRecipeData({
                photo: '',
                ingredients: '',
                method: '',
                calories: ''
            });
            onClose(); // Close the form after successfully adding the recipe
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (!isVisible) return null; // Do not display the form if isVisible is false
    return (
        <div style={{ display: isVisible ? 'block' : 'none' }}> {/* Show the form if isVisible is true */}
            <h2>Add recipe</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Meal photo:
                    <input
                        type="text"
                        name="photo"
                        value={recipeData.photo}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Ingredients:
                    <textarea
                        name="ingredients"
                        value={recipeData.ingredients}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Cooking:
                    <textarea
                        name="method"
                        value={recipeData.method}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Calories:
                    <input
                        type="text"
                        name="calories"
                        value={recipeData.calories}
                        onChange={handleInputChange}
                    />
                </label>
                <button type="submit">Add Recipe</button>
            </form>
        </div>
    );
};

export default RecipeForm;
