import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Добавляем импорт axios

const RecipeList = () => {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        fetchRecipes();
    }, []);

    const fetchRecipes = () => {
        axios.get('http://localhost:3000/api/recipes/my-recipes')
            .then(res => {
                setRecipes(res.data.recipes);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleRefresh = () => {
        fetchRecipes();
    };

    return (
        <div className="recipe-list">
            <h2>My Recipes</h2>
            <ul>
                {recipes.map(recipe => (
                    <li key={recipe.id}>{recipe.name}</li>
                ))}
            </ul>
            <button onClick={handleRefresh}>Refresh Recipes</button>
        </div>
    );
}

export default RecipeList;
