    return (
        <div className="shopping-cart">
            <h2>Shopping Cart</h2>
            <ul>
                {ingredients.map((ingredient) => (
                    <IngredientItem
                        key={ingredient.id}
                        ingredient={ingredient}
                        onMarkAsPurchased={onMarkAsPurchased}
                        onRemoveFromBasket={onRemoveFromBasket}
                    />
                ))}
            </ul>
        </div>
    );
};

