import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ShoppingCart = () => {
    const [basket, setBasket] = useState([]);

    useEffect(() => {
        const fetchBasket = async () => {
            try {
                // Добавляем задержку 1 секунду перед получением корзины
                await new Promise(resolve => setTimeout(resolve, 1000));
                const response = await axios.get('http://localhost:3000/api/recipes/get-basket');
                setBasket(response.data.basket);
            } catch (error) {
                console.log(error);
            }
        };

        fetchBasket();
    }, []);

    const handleMarkAsPurchased = async (ingredientId) => {
        try {
            // Добавляем задержку 1 секунду перед отправкой запроса
            await new Promise(resolve => setTimeout(resolve, 1000));
            const response = await axios.put('http://localhost:3000/api/recipes/mark-as-purchased', { ingredientId });
            const updatedBasket = basket.map(item => {
                if (item.id === ingredientId) {
                    return { ...item, purchased: true };
                }
                return item;
            });
            setBasket(updatedBasket);
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className="shopping-cart">
            <h2>Shopping Cart</h2>
            <ul>
                {basket.map(item => (
                    <li key={item.id}>
                        {item.name} {item.purchased ? "(Purchased)" : ""}
                        {!item.purchased && (
                            <button onClick={() => handleMarkAsPurchased(item.id)}>Mark as Purchased</button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ShoppingCart;
