export const fetchCartItems = async () => {
    try {
        const response = await fetch('/api/recipes/get-basket');
        const data = await response.json();
        return data.basket;
    } catch (error) {
        console.error('Error:', error);
    }
};

export const fetchCaloriePlan = async () => {
    try {
        const response = await fetch('/api/recipes/get-plan');
        const data = await response.json();
        return data.plan;
    } catch (error) {
        console.error('Error:', error);
    }
};
