import React, { useState, useEffect } from 'react';

function Plan() {
    const [caloriePlan, setCaloriePlan] = useState(null);

    useEffect(() => {
        fetchCaloriePlan();
    }, []);

    const fetchCaloriePlan = async () => {
        try {
            const response = await fetch('/api/recipes/get-plan');
            const data = await response.json();
            setCaloriePlan(data.plan);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className="plan">
            <h2>Calorie Plan</h2>
            {caloriePlan && <p>{caloriePlan} calories</p>}
        </div>
    );
}

export default Plan;
