import React, { useState, useEffect } from 'react';

const Schedule = () => {
    const [mealSchedule, setMealSchedule] = useState(null);

    useEffect(() => {
        fetch('/api/recipes/get-schedule')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setMealSchedule(data.schedule);
            })
            .catch(error => console.error('Error fetching meal schedule:', error));
    }, []);

    return (
        <div className="schedule">
            <h2>Meal Schedule</h2>
            {mealSchedule ? (
                <ul>
                    {Object.keys(mealSchedule).map((day) => (
                        <li key={day}>
                            <strong>{day}:</strong> {mealSchedule[day]}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default Schedule;
