import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlanSchedule = () => {
    const [schedule, setSchedule] = useState({
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: "",
        Saturday: "",
        Sunday: ""
    });
    const [savedSchedule, setSavedSchedule] = useState({
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: "",
        Saturday: "",
        Sunday: ""
    });

    useEffect(() => {
        fetchSchedule();
    }, []);

    const fetchSchedule = () => {
        const savedScheduleData = JSON.parse(localStorage.getItem('savedSchedule'));
        if (savedScheduleData) {
            setSavedSchedule(savedScheduleData);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSchedule(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:3000/api/recipes/set-schedule', schedule)
            .then(res => {
                console.log(res.data.message);
                setSavedSchedule(schedule);
                localStorage.setItem('savedSchedule', JSON.stringify(schedule));
            })
            .catch(err => {
                console.log(err);
            });
    };

    return (
        <div className="plan-schedule">
            <h2>Plan Schedule</h2>
            <form onSubmit={handleSubmit}>
                {Object.keys(schedule).map(day => (
                    <div key={day} className="schedule-input">
                        <label>{day}:</label>
                        <input type="text" name={day} value={schedule[day]} onChange={handleChange} />
                        <span className="saved-schedule">{savedSchedule[day]}</span>
                    </div>
                ))}
                <button type="submit">Save Schedule</button>
            </form>
        </div>
    );
}

export default PlanSchedule;
