/*
import React, { useState } from 'react';
import RecipeForm from "./RecipeForm";
import MyMenu from "./MyMenu11";

const Menu = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isRecipeFormVisible, setRecipeFormVisible] = useState(false);
    const [recipes, setRecipes] = useState([]);
    const [showMyMenu, setShowMyMenu] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const handleAddRecipeClick = () => {
        setRecipeFormVisible(true);
    };

    const handleRecipeSave = (newRecipe) => {
        setRecipes([...recipes, newRecipe]);
        setRecipeFormVisible(false);
        setShowMyMenu(true); // Showing my menu after recipe saving
    };

    const handleShowMyMenu = () => {
        setIsOpen(false);
        setShowMyMenu(true);
        toggleMenu();
    };

    return (
        <div className="menu">
            <button className="menu-button" onClick={toggleMenu}>
                Menu
            </button>
            {isOpen && (
                <div className="menu-items">
                    <p onClick={handleShowMyMenu}>My menu</p>
                    <p onClick={handleAddRecipeClick}>Add Recipe</p>
                    <p onClick={toggleMenu}>Schedule</p>
                    <p onClick={toggleMenu}>My plan</p>
                </div>
            )}
            {isRecipeFormVisible && <RecipeForm isVisible={isRecipeFormVisible} onClose={() => setRecipeFormVisible(false)} onSave={handleRecipeSave} />}
            {showMyMenu && <MyMenu recipes={recipes} />}
        </div>
    );
};

export default Menu;
*/
