import React, { useState } from 'react';

const Menu = () => {
    const [isOpen, setIsOpen] = useState(false);

    // Функция для открытия/закрытия меню
    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="menu">
            <button className="menu-button" onClick={toggleMenu}>
                Меню
            </button>
            {/* Если меню открыто, показываем его содержимое */}
            {isOpen && (
                <div className="menu-items">
                    <p onClick={toggleMenu}>Моё меню</p>
                    <p onClick={toggleMenu}>Добавить рецепт</p>
                    <p onClick={toggleMenu}>Расписания</p>
                    <p onClick={toggleMenu}>Мой план калорий на день</p>
                </div>
            )}
        </div>
    );
};

export default Menu;
