import React, { useState, useEffect } from 'react';
import { fetchCartItems } from '../api';

function Cart() {
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetchCartItems()
            .then(data => setItems(data || [])) // Проверяем, если данные существуют, иначе устанавливаем пустой массив
            .catch(error => console.error('Error:', error));
    }, []);

    return (
        <div className="cart">
            <button onClick={() => console.log("Cart clicked")}>Cart</button>
            <div className="cart-items">
                {items.map(item => (
                    <div key={item.id}>{item.name}</div>
                ))}
            </div>
        </div>
    );
}

export default Cart;
