import React, { useState } from 'react';

const AddRecipeForm = ({ onAddRecipe }) => {
    const [recipe, setRecipe] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipe({ ...recipe, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddRecipe(recipe);
        setRecipe({});
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Name" onChange={handleChange} />
            <input type="text" name="image" placeholder="Image URL" onChange={handleChange} />
            <input type="text" name="ingredients" placeholder="Ingredients" onChange={handleChange} />
            <input type="text" name="calories" placeholder="Calories" onChange={handleChange} />
            <textarea name="instructions" placeholder="Instructions" onChange={handleChange} />
            <button type="submit">Add Recipe</button>
        </form>
    );
};

export default AddRecipeForm;
