import React, { useState, useEffect } from 'react';

function Basket() {
    const [basket, setBasket] = useState([]);

    useEffect(() => {
        fetchBasket();
    }, []);

    const fetchBasket = () => {
        fetch('/get-basket')
            .then(response => response.json())
            .then(data => {
                setBasket(data.basket);
            })
            .catch(error => console.error('Error fetching basket:', error));
    };

    const removeFromBasket = (ingredientId) => {
        fetch(`/remove-from-basket/${ingredientId}`, {
            method: 'DELETE'
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    fetchBasket(); // Refresh basket after removal
                } else {
                    console.error('Failed to remove ingredient:', data.message);
                }
            })
            .catch(error => console.error('Error removing ingredient:', error));
    };

    const markAsPurchased = (ingredientId) => {
        fetch(`/mark-as-purchased/${ingredientId}`, {
            method: 'PUT'
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    fetchBasket(); // Refresh basket after marking
                } else {
                    console.error('Failed to mark ingredient as purchased:', data.message);
                }
            })
            .catch(error => console.error('Error marking ingredient as purchased:', error));
    };

    return (
        <div>
            <h2>Shopping Basket</h2>
            <ul>
                {basket.map(item => (
                    <li key={item.id}>
                        {item.name} - {item.purchased ? 'Purchased' : 'Not Purchased'}
                        <button onClick={() => removeFromBasket(item.id)}>Remove</button>
                        <button onClick={() => markAsPurchased(item.id)}>Mark as Purchased</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Basket;
