import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import RecipeList from './components/RecipeList';
import ShoppingBasket from './components/ShoppingBasket';
// Import MealSchedule if needed

function App() {
    const [recipes, setRecipes] = useState([]); // Stores recipes data
    const [basket, setBasket] = useState([]); // Stores shopping basket data
    const [schedule, setSchedule] = useState(null); // Stores meal schedule (optional)

    // Fetch recipes data on component mount
    useEffect(() => {
        const fetchRecipes = async () => {
            const response = await fetch('/api/recipes/my-recipes'); // Assuming your backend route
            const data = await response.json();
            setRecipes(data.recipes);
        };

        fetchRecipes();
    }, []);

    // Functions to handle adding/removing ingredients from basket (implement similar logic for schedule)
    const addToBasket = (ingredient) => {
        setBasket([...basket, ingredient]);
    };

    const removeFromBasket = (ingredientId) => {
        setBasket(basket.filter((item) => item.id !== ingredientId));
    };

    return (
        <div className="App">
            <Header />
            <main>
                <RecipeList recipes={recipes} onAddToBasket={addToBasket} />
                <ShoppingBasket basket={basket} onRemove={removeFromBasket} />
                {/* Add MealSchedule component here if needed */}
            </main>
        </div>
    );
}

export default App;
