import React, { useState, useEffect } from 'react';
import Menu from './components/Menu';
import AddRecipeForm from './components/AddRecipeForm';
import Schedule from './components/Schedule';
import Plan from './components/Plan';
import ShoppingCart from './components/ShoppingCart';

const App = () => {
    const [ingredients, setIngredients] = useState([]);

    useEffect(() => {
        // Fetch shopping basket data from backend on component mount
        fetchBasketData();
    }, []);

    const fetchBasketData = () => {
        // Fetch shopping basket data from backend
        fetch('/api/recipes/get-basket')
            .then((response) => response.json())
            .then((data) => setIngredients(data.basket))
            .catch((error) => console.error('Error fetching shopping basket data:', error));
    };

    const handleAddRecipe = (recipe) => {
        // Send recipe data to backend for addition
        fetch('/api/recipes/create', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(recipe),
        })
            .then((response) => response.json())
            .then((data) => console.log('Recipe added successfully:', data))
            .catch((error) => console.error('Error adding recipe:', error));
    };

    const handleMarkAsPurchased = (ingredientId) => {
        // Send ingredient ID to backend to mark as purchased
        fetch('/api/recipes/mark-as-purchased', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ ingredientId }),
        })
            .then(() => fetchBasketData())
            .catch((error) => console.error('Error marking ingredient as purchased:', error));
    };

    const handleRemoveFromBasket = (ingredientId) => {
        // Send ingredient ID to backend to remove from basket
        fetch('/api/recipes/remove-from-basket', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ ingredientId }),
        })
            .then(() => fetchBasketData())
            .catch((error) => console.error('Error removing ingredient from basket:', error));
    };

    return (
        <div className="app">
            <Menu />
            <div className="main">
                <AddRecipeForm onAddRecipe={handleAddRecipe} />
                <Schedule />
                <Plan />
                <ShoppingCart
                    ingredients={ingredients}
                    onMarkAsPurchased={handleMarkAsPurchased}
                    onRemoveFromBasket={handleRemoveFromBasket}
                />
            </div>
        </div>
    );
};

export default App;
