import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Menu from './components/Menu';
import Basket from './components/Basket';
import RecipeList from './components/RecipeList';
import AddRecipePage from './components/pages/AddRecipePage';
import PlanSchedule from './components/PlanSchedule';
import RecipePage from './components/pages/RecipePage';
import Plan from './components/Plan';
import './index.css';

function App() {
    const [showBasket, setShowBasket] = useState(false);
    const location = useLocation();

    useEffect(() => {
        setShowBasket(false); // Hide basket on location change
    }, [location]);

    return (
        <Router>
            <div className="App">
                <Menu />
                <div className="content">
                    <Routes>
                        <Route path="/my-recipes" element={<RecipeList />} />
                        <Route path="/add-recipe" element={<AddRecipePage />} />
                        <Route path="/schedule" element={<PlanSchedule />} />
                        <Route path="/plan" element={<Plan />} />
                        <Route path="/recipe/:id" element={<RecipePage />} />
                    </Routes>
                </div>
                {showBasket && <Basket />} {/* Show basket */}
                {location.pathname === '/' && <Footer />} {/* Show logo on home page */}
            </div>
        </Router>
    );
}

const Footer = () => {
    return (
        <footer className="footer">
            <div className="footer-logo-container">
                <img src="../public/MealPlannerlogo.png" alt="Logo" className="footer-logo" />
            </div>
        </footer>
    );
};

export default App;
