import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Basket = () => {
    const [basket, setBasket] = useState([]);

    useEffect(() => {
        const fetchBasket = async () => {
            try {
                const response = await axios.get('/api/recipes/get-basket');
                setBasket(response.data.basket);
            } catch (error) {
                console.error('Error fetching basket:', error);
            }
        };

        fetchBasket();
    }, []);

    const handleAddToBasket = async (ingredient) => {
        try {
            const response = await axios.post('/api/recipes/add-to-basket', { ingredient });
            console.log(response.data);
        } catch (error) {
            console.error('Error adding to basket:', error);
        }
    };

    const handleRemoveFromBasket = async (ingredientId) => {
        try {
            const response = await axios.delete('/api/recipes/remove-from-basket', { data: { ingredientId } });
            console.log(response.data);
        } catch (error) {
            console.error('Error removing from basket:', error);
        }
    };

    const handleMarkAsPurchased = async (ingredientId) => {
        try {
            const response = await axios.put('/api/recipes/mark-as-purchased', { ingredientId });
            console.log(response.data);

        } catch (error) {
            console.error('Error marking as purchased:', error);
        }
    };

    return (
        <div>
            <h2>Basket</h2>
            <ul>
                {basket.map(ingredient => (
                    <li key={ingredient.id}>
                        {ingredient.name} - {ingredient.quantity}
                        <button onClick={() => handleRemoveFromBasket(ingredient.id)}>Remove</button>
                        <button onClick={() => handleMarkAsPurchased(ingredient.id)}>Mark as Purchased</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Basket;
