import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlanSchedule = () => {
    const [schedule, setSchedule] = useState({
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: "",
        Saturday: "",
        Sunday: ""
    });

    useEffect(() => {
        fetchSchedule();
    }, []);

    const fetchSchedule = () => {
        axios.get('/api/recipes/get-schedule')
            .then(res => {
                setSchedule(res.data.schedule);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSchedule(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('/api/recipes/set-schedule', schedule)
            .then(res => {
                console.log(res.data.message);
            })
            .catch(err => {
                console.log(err);
            });
    };

    return (
        <div className="plan-schedule">
            <h2>Plan Schedule</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Monday:</label>
                    <input type="text" name="Monday" value={schedule.Monday} onChange={handleChange} />
                </div>
                <div>
                    <label>Tuesday:</label>
                    <input type="text" name="Tuesday" value={schedule.Tuesday} onChange={handleChange} />
                </div>
                <div>
                    <label>Wednesday:</label>
                    <input type="text" name="Wednesday" value={schedule.Wednesday} onChange={handleChange} />
                </div>
                <div>
                    <label>Thursday:</label>
                    <input type="text" name="Thursday" value={schedule.Thursday} onChange={handleChange} />
                </div>
                <div>
                    <label>Friday:</label>
                    <input type="text" name="Friday" value={schedule.Friday} onChange={handleChange} />
                </div>
                <div>
                    <label>Saturday:</label>
                    <input type="text" name="Saturday" value={schedule.Saturday} onChange={handleChange} />
                </div>
                <div>
                    <label>Sunday:</label>
                    <input type="text" name="Sunday" value={schedule.Sunday} onChange={handleChange} />
                </div>
                <button type="submit">Save Schedule</button>
            </form>
        </div>
    );
}

export default PlanSchedule;
