import React from 'react';

const IngredientItem = ({ ingredient, onMarkAsPurchased, onRemoveFromBasket }) => {
    return (
        <li>
            {ingredient.name}
            <button onClick={() => onMarkAsPurchased(ingredient.id)}>Mark as Purchased</button>
            <button onClick={() => onRemoveFromBasket(ingredient.id)}>Remove</button>
        </li>
    );
};

export default IngredientItem;
