import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const RecipePage = () => {
    const [recipe, setRecipe] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        if (id) {
            fetchRecipe(id);
        }
    }, [id]);

    const fetchRecipe = (recipeId) => {
        axios.get(`http://localhost:3000/api/recipes/${recipeId}`)
            .then(res => {
                const updatedRecipe = { ...res.data.recipe, image: `http://localhost:3000${res.data.recipe.image}` };
                setRecipe(updatedRecipe);
            })
            .catch(err => {
                console.log(err);
            });
    };

    if (!recipe) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>{recipe.name}</h1>
            {/* Выводим путь к изображению для отладки */}
            <p>Image Path: {recipe.image}</p>
            {/* Показываем изображение для отладки */}
            <img src={recipe.image} alt={recipe.name} onLoad={() => console.log("Image loaded")} onError={() => console.log("Image failed to load")} />
            <p>Ingredients: {recipe.ingredients}</p>
            <p>Calories: {recipe.calories}</p>
            <p>Instructions: {recipe.instructions}</p>
        </div>
    );
}

export default RecipePage;
