import React, { useState } from 'react';
import axios from 'axios';


const AddRecipeForm = () => {
    const [formData, setFormData] = useState({
        image: '',
        name: '',
        ingredients: '',
        calories: '',
        instructions: ''
    });

    const handleChange = e => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = e => {
        e.preventDefault();
        axios.post('http://localhost:3000/api/recipes/create', formData)
            .then(response => {
                console.log('Recipe added:', response.data);
                // Clear form fields
                setFormData({
                    image: '',
                    name: '',
                    ingredients: '',
                    calories: '',
                    instructions: ''
                });
            })
            .catch(error => {
                console.error('Error adding recipe:', error);
            });
    };


    return (
        <div className="add-recipe-form">
            <h2>Add Recipe</h2>
            <form onSubmit={handleSubmit}>
                <input type="image" name="image" value={formData.image} onChange={handleChange} placeholder="Image URL" />
                <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" />
                <input type="text" name="ingredients" value={formData.ingredients} onChange={handleChange} placeholder="Ingredients" />
                <input type="text" name="calories" value={formData.calories} onChange={handleChange} placeholder="Calories" />
                <textarea name="instructions" value={formData.instructions} onChange={handleChange} placeholder="Instructions"></textarea>
                <button type="submit">Add Recipe</button>
            </form>
        </div>
    );
}

export default AddRecipeForm;
