import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Basket.css'; // импорт стилей

const Basket = ({ addToBasket }) => {
    const [basket, setBasket] = useState([]);
    const [ingredientName, setIngredientName] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        fetchBasket();
    }, []);

    const fetchBasket = () => {
        axios.get('http://localhost:3000/api/recipes/get-basket')
            .then(res => {
                setBasket(res.data.basket || []);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleAddToBasket = () => {
        if (!ingredientName.trim()) {
            setError('Please enter an ingredient name');
            return;
        }

        axios.post('http://localhost:3000/api/recipes/add-to-basket', { ingredient: { name: ingredientName } })
            .then(res => {
                setIngredientName('');
                setError('');
                setBasket(res.data.basket || []);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleRemoveFromBasket = (id) => {
        axios.delete(`http://localhost:3000/api/recipes/remove-from-basket/${id}`)
            .then(res => {
                setBasket(basket.filter(ingredient => ingredient.id !== id));
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleMarkAsPurchased = (id) => {
        axios.put(`http://localhost:3000/api/recipes/mark-as-purchased/${id}`)
            .then(res => {
                setBasket(basket.map(ingredient =>
                    ingredient.id === id ? { ...ingredient, purchased: true } : ingredient
                ));
            })
            .catch(err => {
                console.log(err);
            });
    };

    return (
        <div className="basket">
            <h2>Shopping Basket</h2>
            {error && <p>{error}</p>}
            <div className="add-ingredient">
                <input type="text" value={ingredientName} onChange={(e) => setIngredientName(e.target.value)} placeholder="Enter ingredient" />
                <button onClick={handleAddToBasket}>Add</button>
            </div>
            <ul>
                {basket.map(ingredient => (
                    <li key={ingredient.id} className={ingredient.purchased ? 'purchased' : ''}>
                        <span>{ingredient.name}</span>
                        <button onClick={() => handleRemoveFromBasket(ingredient.id)}>Remove</button>
                        {!ingredient.purchased && <button onClick={() => handleMarkAsPurchased(ingredient.id)}>Mark as Purchased</button>}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Basket;
