import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Plan = () => {
    const [caloriePlan, setCaloriePlan] = useState(null);
    const [calories, setCalories] = useState('');
    const [savedCalories, setSavedCalories] = useState('');

    useEffect(() => {
        fetchCaloriePlan();
    }, []);

    const fetchCaloriePlan = () => {
        axios.get('http://localhost:3000/api/recipes/get-plan') // изменено: указываем правильный адрес и порт сервера
            .then(res => {
                setCaloriePlan(res.data.plan);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleChange = (e) => {
        setCalories(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:3000/api/recipes/set-plan', { calories: parseInt(calories) }) // изменено: указываем правильный адрес и порт сервера
            .then(res => {
                setSavedCalories(calories); // изменение: устанавливаем сохранённые калории для отображения
                console.log(res.data.message);
            })
            .catch(err => {
                console.log(err);
            });
    };

    return (
        <div className="plan">
            <h2>Calorie Plan</h2>
            {caloriePlan ? <p>Your daily calorie plan is {caloriePlan} calories.</p> : <p>Loading...</p>}
            <form onSubmit={handleSubmit}>
                <div className="calorie-input">
                    <label>Set daily calories:</label>
                    <input type="number" value={calories} onChange={handleChange} /> {/* изменение: используем type="number" */}
                    <button type="submit">Save</button>
                    {savedCalories && <p>Successfully saved plan: {savedCalories} calories</p>}
                </div>
            </form>
        </div>
    );
};

export default Plan;
