import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useHistory} from 'react-router-dom';
import Menu from './components/Menu';
import Basket from './components/Basket';
import RecipeList from './components/RecipeList';
import AddRecipePage from './components/pages/AddRecipePage';
import PlanSchedule from './components/PlanSchedule';
import RecipePage from './components/pages/RecipePage'; // Импортируем новую страницу RecipePage
import Plan from './components/Plan';
import './index.css';

function App() {
    const [showBasket, setShowBasket] = useState(false);
    const [showLogo, setShowLogo] = useState(true);
    const [currentPath, setCurrentPath] = useState('');
    const history = useHistory();

    useEffect(() => {
        // Установка текущего пути при загрузке приложения
        setCurrentPath(window.location.pathname);
        // Проверка пути при каждом изменении URL
        window.onpopstate = () => {
            setCurrentPath(window.location.pathname);
        };
    }, []);

    useEffect(() => {
        // Проверяем текущий путь
        setShowLogo(currentPath === '/');
    }, [currentPath]);

    const toggleBasket = () => {
        setShowBasket(!showBasket);
    };

    useEffect(() => {
        // Проверяем, является ли текущий путь главной страницей
        setShowLogo(history.location.pathname === '/');

        const unlisten = history.listen((location, action) => {
            // Скрываем логотип при переходе на другую страницу
            setShowLogo(location.pathname === '/');
        });

        return () => {
            unlisten();
        };
    }, [history]);


    return (
        <Router>
            <div className="App">
                <Menu />
                <div className="content">
                    <Routes>
                        <Route path="/my-recipes" element={<RecipeList />} />
                        <Route path="/add-recipe" element={<AddRecipePage />} />
                        <Route path="/schedule" element={<PlanSchedule />} />
                        <Route path="/plan" element={<Plan />} />
                        <Route path="/recipe/:id" element={<RecipePage />} />
                    </Routes>
                </div>
                <button onClick={toggleBasket}>Toggle Basket</button>
                {showBasket && <Basket />}
                {showLogo && (
                    <footer className="footer">
                        <img src="/MealPlannerlogo.png" alt="Logo" className="footer-logo" />
                    </footer>
                )}
            </div>
        </Router>
    );
}


export default App;
