// RecipeForm.js

import React, { useState } from 'react';

const RecipeForm = ({isVisible, onClose}) => {
    // Состояние для хранения данных о рецепте
    const [recipeData, setRecipeData] = useState({
        photo: '',
        ingredients: '',
        method: '',
        calories: ''
    });

    // Обработчик изменений в полях формы
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setRecipeData({ ...recipeData, [name]: value });
    };

    // Обработчик отправки формы
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Отправка данных на сервер
            const response = await fetch('/api/recipe/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(recipeData)
            });
            if (!response.ok) {
                throw new Error('Adding error');
            }
            // Success
            console.log('Recipe Added');
            // Сброс данных формы
            setRecipeData({
                photo: '',
                ingredients: '',
                method: '',
                calories: ''
            });
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (!isVisible) return null; // Не отображаем форму, если isVisible равно false
    return (
        <div>
            <h2>Add recipe</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Meal photo:
                    <input
                        type="text"
                        name="photo"
                        value={recipeData.photo}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Ingredients:
                    <textarea
                        name="ingredients"
                        value={recipeData.ingredients}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Cooking:
                    <textarea
                        name="method"
                        value={recipeData.method}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Калорийность:
                    <input
                        type="text"
                        name="calories"
                        value={recipeData.calories}
                        onChange={handleInputChange}
                    />
                </label>
                <button type="submit">Add Recipe</button>
            </form>
        </div>
    );
};

export default RecipeForm;
