const RecipePage = ({ match }) => {
    const [recipe, setRecipe] = useState(null);

    useEffect(() => {
        console.log(match.params.id); // Проверяем, что id передается корректно
        if (match && match.params && match.params.id) {
            fetchRecipe(match.params.id);
        }
    }, [match]);

    const fetchRecipe = (recipeId) => {
        axios.get(`http://localhost:3000/api/recipes/${recipeId}`)
            .then(res => {
                console.log(res.data); // Проверяем, что данные рецепта получены
                setRecipe(res.data.recipe);
            })
            .catch(err => {
                console.log(err);
            });
    };

    if (!recipe) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>{recipe.name}</h1>
            <img src={recipe.image} alt={recipe.name} />
            <p>Ingredients: {recipe.ingredients}</p>
            <p>Calories: {recipe.calories}</p>
            <p>Instructions: {recipe.instructions}</p>
        </div>
    );
}
